/*Scrollbar pernsonalizada*/
body::-webkit-scrollbar {
width: 8px;
}

body::-webkit-scrollbar-track {
background: #3d3d3d;
}

body::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, #FF8951 25.05%, #FF1720 75.1%, #8E1723 151.26%);
}

/*Remover Padding default*/
.elementor-widget-text-editor p:last-child{ margin-bottom:0px;

}

/*Remover scroll horizontal*/
html, body{
 width: 100%;
 overflow-x: hidden;
}

/* Alterar cor da seleção */
::selection {
    -webkit-text-fill-color: #fff;
    color: #dad7c7;
    background: #FF1720;
}

